      url: <span class="token variable">${{ steps.deployment.outputs.page_url }</span><span class="token punctuation">}</span>
    runs-on: ubuntu-latest
    needs: build
    steps:
      - name: Deploy to GitHub Pages
        id: deployment
        uses: actions/deploy-pages@v2

